<script>
import axios from 'axios'
import '../assets/scss/style.scss'
// gsap
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
// import { MotionPathPlugin } from "gsap/MotionPathPlugin";

// firebase to get products
import { initializeApp } from 'firebase/app'
import { getStorage, ref, getDownloadURL } from "firebase/storage"
import '../assets/scss/style.scss'
if (process.client) {
  gsap.registerPlugin(ScrollTrigger)
  // gsap.registerPlugin(MotionPathPlugin);
}

// import BoxCat1 from '../components/common/BoxCat1'



export default {
  name: 'App',
  // components: {
  //   BoxCat1
  // },
  data () {
    return {
      loginForm: {
        email: '',
        password: '',
      },
      users: [],
      accessToken: '',
      msg: 'change point',
      products: [],
      colormeURL: "https://gypsyworks.shop-pro.jp/?pid=",
    }
  },
  mutations: {
  },
  created() {
    console.log("get inside categories in created")
    const firebaseConfig = {
      storageBucket: 'gs://gypsyworks-5cf3e.appspot.com'
    }
      // Initialize Firebase
    const app = initializeApp(firebaseConfig)
    // Initialize Cloud Storage and get a reference to the service
    const storage = getStorage(app)
    // Create a storage reference from our storage service
    const myfileRef = ref(storage, 'my-file')
    // URL経由でダウンロード
    getDownloadURL(myfileRef)
      .then((url) => {
        console.log(url)
        fetch(url)
          .then(result => result.json())
            .then((output) => {
              for(const i in output.products) {
                this.products.push(output.products[i]);
              }
              // console.log(this.products);
            }).catch(err => console.error(err));
      })
      .catch((error) => {
        console.log(error)
      })
  },
  mounted() {
    this.gsapHorizon();
    // this.svgAnimationA();
  },
  methods: {
    prodLink (id) {
      return "https://gypsyworks.shop-pro.jp/?pid=" + id;
    },
    gsapHorizon () {
      // window.addEventListener("load", function(){
        const horizontalize  = document.querySelector(".horizontalize");
        const columnContainer  = document.querySelector(".column_container");
        const columns = document.querySelectorAll(".column");
        const num   = columns.length;

        // 横幅を指定
        gsap.set(columnContainer,  { width: num * 100 + "%" });
        gsap.set(columnContainer,  { x: (num-1) * -100 + "vw" });
        gsap.set(columns, { width: 100 / num + "%" }); // 横スク用に大きくした親要素が３00%なら、子要素１つが100%

        const scrollTween = gsap.to(columns, {
          // xPercent: 100 * ( num - 1 ), // x方向に移動させる
          x: () => (columnContainer.clientWidth - horizontalize.clientWidth),
          ease: "none", // <-- IMPORTANT!
          scrollTrigger: {
            trigger: horizontalize, // トリガー
            start: "top top", // 開始位置
            // end: "+=1000", // 終了位置
            end: () => `+=${columnContainer.clientWidth - horizontalize.clientWidth}`,
            pin: true, // ピン留め=スクロール中指定要素を固定
            scrub: true, // スクロール量に応じて動かす
            // scrub: 2, // アニメーションがスクロールに反応するまでの時間
            // snap: { // キリの良い位置へ移動させる
            //   snapTo: 1 / ( num - 1 ),
            //   duration: 2,
            // },
            invalidateOnRefresh: true,
            // horizontal: true, // スクロールのむき
            // preventOverlaps: true,
            onEnter: ()=> console.log("for debug column amount is...: " + num),
          }
        });
        // すべてのパスを取得
        const svg1Els = document.querySelectorAll(".svg1");
        gsap.set(svg1Els, {transformOrigin:'50% 50%'});
        const tl1 = gsap.timeline({
          // strokedasharray: 11110,
          // strokeDashoffset: 1300,
          scrollTrigger:{
            trigger: ".column2",
            start: "center left",
            end: "left left",
            scrub:3,
            markers:false,
            containerAnimation: scrollTween,
            toggleActions:'play none none reverse',
            once: true,
          }
        });
        tl1.fromTo(svg1Els,
          {'stroke-dashoffset': '1300px'},
          {'stroke-dashoffset': '0',duration:1 ,stagger:0.1}
        )
        const svg2Els = document.querySelectorAll(".svg2");
        gsap.set(svg2Els, {transformOrigin:'50% 50%'});
        const tl2 = gsap.timeline({
          // strokedasharray: 11110,
          // strokeDashoffset: 1300,
          scrollTrigger:{
            trigger: ".column3",
            start: "center left",
            end: "left left",
            scrub:3,
            markers:false,
            containerAnimation: scrollTween,
            toggleActions:'play none none reverse',
            once: true,
          }
        });
        tl2.fromTo(svg2Els,
          {'stroke-dashoffset': '1300px'},
          {'stroke-dashoffset': '0',duration:1 ,stagger:0.1}
        )
        const svg3Els = document.querySelectorAll(".svg3");
        gsap.set(svg3Els, {transformOrigin:'50% 50%'});
        const tl3 = gsap.timeline({
          // strokedasharray: 11110,
          // strokeDashoffset: 1300,
          scrollTrigger:{
            trigger: ".column4",
            start: "center left",
            end: "left left",
            scrub:3,
            markers:false,
            containerAnimation: scrollTween,
            toggleActions:'play none none reverse',
            once: true,
          }
        });
        tl3.fromTo(svg3Els,
          {'stroke-dashoffset': '1300px'},
          {'stroke-dashoffset': '0',duration:1 ,stagger:0.1}
        )
        // const newscolumn = document.querySelectorAll(".column5");
        const newscolumn = gsap.utils.toArray('.column5');
        gsap.fromTo(newscolumn, {
          backgroundColor: "rgba(14, 0F, 0E, 1)",
        },
        {
          backgroundColor: "#F1EDDC",
          delay: 10,
          // duration: 2,
