          // ease: "none", // <-- IMPORTANT!
          scrollTrigger: {
            trigger: newscolumn, // トリガー
            start: "20% left",
            end: "left left",
            scrub: true, // スクロール量に応じて動かす
            containerAnimation: scrollTween,
          }
        });
        const body = gsap.utils.toArray('body');
        gsap.fromTo(body, {
          backgroundColor: "#140F0E",
        },
        {
          backgroundColor: "#F1EDDC",
          delay: 10,
          // duration: 2,
          // ease: "power2.inOut",
          // ease: "none", // <-- IMPORTANT!
          scrollTrigger: {
            trigger: newscolumn, // トリガー
            start: "20% left",
            end: "left left",
            scrub: true, // スクロール量に応じて動かす
            containerAnimation: scrollTween,
          }
        });
    },
    getProducts () {
      axios.get('https://api.shop-pro.jp/v1/products', {
        headers: {
          'Authorization': `Bearer ${this.$store.state.token.access_toke}`
        }
      })
      .then((res) => {
        console.log(res.data)
      })
      .catch((error) => {
        console.error(error)
      })
    },
  }
